import { ItemInterface } from "../pages/Home";
import useLocalStorage from "../hooks/useLocalStorage";

const useFavorites = (newItem: ItemInterface) => {
    const prevItem: ItemInterface[] = useLocalStorage('favorites');
    const found = prevItem.some(item => item.id !== newItem.id);
    if (found) {
    localStorage.setItem('favorites', JSON.stringify([...prevItem, newItem]));
    return true;
    } else {
        return false;
    }
}

export default useFavorites;
