import { useEffect, useState } from "react";
import { ItemInterface } from "../pages/Home";

const useUpdateLocalStorage = (key: string, item?: ItemInterface) => {
    
    const getData = localStorage.getItem(key);
    const prevData = JSON.parse(getData!);
    const [list, setList] = useState<ItemInterface[]>(prevData);

 //   const filteredData = prevData.filter(data => data.id === id);

    useEffect(() => {
        if (item !== undefined) {
        if (list) {
            const updatedList = [...list, item];
            setList(updatedList);
            localStorage.setItem(key, JSON.stringify(updatedList));
        }
    }
    }, [list, key, item]);

    return {list};
}
 
export default useUpdateLocalStorage;
