import { useEffect, useState } from "react";
import useFetch from "../hooks/useFetch";
import { useLocation } from "react-router-dom";
import { Console } from "console";

interface MealData {
    title: string;
    servings: number;
    readyInMinutes: number;
    instructions: string;
    image: string;
    dishTypes: [];
    id: number;
}


const Meal = () => {
    const APIKEY = 'ae98638f897c4eb79d6f212f141affb8';
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const foodId = queryParams.get('id');
    const {data, loading, error} = useFetch(`https://api.spoonacular.com/recipes/${foodId}/information?apiKey=${APIKEY}&includeNutrition=false`);
    const [mealRecipe, setMealRecipe] = useState();
    const { title, servings, readyInMinutes, instructions, image, dishTypes, id}: MealData = data;

    useEffect(() => {
        if (data) {
        setMealRecipe(data);
        }
    }, [data])


    return ( 
        <div className="foodRecipeContainer">

                <div className="titleContainer">
                    <span className="title">{ title }</span>
                </div>

        </div>
     );
}
 
export default Meal;
