import { useEffect, useState } from "react";
import useFetch from "../hooks/useFetch";
import { useLocation } from "react-router-dom";
import { Console } from "console";


interface Ingredients {
    id: number;
    image: string;
    original: string;
}

interface MealData {
    title: string;
    servings: number;
    readyInMinutes: number;
    instructions: string;
    image: string;
    dishTypes: [];
    id: number;
    extendedIngredients: Ingredients[]
}



const Meal = () => {
    const APIKEY = 'ae98638f897c4eb79d6f212f141affb8';
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const foodId = queryParams.get('id');
    const {data, loading, error} = useFetch(`https://api.spoonacular.com/recipes/${foodId}/information?apiKey=${APIKEY}&includeNutrition=false`);
    const [mealRecipe, setMealRecipe] = useState();
    const { title, servings, readyInMinutes, instructions, image, dishTypes, id, extendedIngredients }: MealData = data;

    useEffect(() => {
        if (data) {
        setMealRecipe(data);
        console.log(data);
        }
    }, [data])


    return ( 
        <div className="foodRecipeContainer">

                <div className="titleContainer">
                    <span className="title">{ title }</span>
                </div>
                <div className="mealInfoContainer">
                    <div className="infoContainer">
                        {dishTypes.map(type => 
                            <span className="info">{ type }</span>
                        )}
                    </div>
                    <div className="infoContainer">
                        <span className="info"></span>
                    </div>
                    <div className="infoContainer">

                    </div>

                </div>
        </div>
     );
}
 
export default Meal;
