import { useEffect, useState } from "react"
import useFetch from "../hooks/useFetch"

const Home = () => {
    const APIKEY = 'ae98638f897c4eb79d6f212f141affb8';
    const [query, setQuery] = useState<string>('chicken');
    const [number, setNumber] = useState<number>(2);


    const {data: itemData, loading, error} = useFetch(`https://api.spoonacular.com/recipes/complexSearch?apiKey=${APIKEY}&query=${query}&number=${number}`);
    const [item, setItem] = useState<object[]>();
    


    const handleQuery = (e: Event | undefined): void => {
        e?.preventDefault();
        if (itemData && itemData.results) {
            setItem(itemData.results);
        }
        console.log(item);
    }

    return (
        <div className="home">'
            <div className="form-container">
            <form onSubmit={() => handleQuery(event)}>
                <input type="text" onChange={(e) => setQuery(e.target.value)} value={query} />
                <button>Search</button>
            </form>
            </div>
        </div>
    )
}

export default Home;
