import { useEffect } from "react";
import useLocalStorage from "../hooks/useLocalStorage";
import { ItemInterface } from "../pages/Home";

const Favorites = () => {
    const { list } = useLocalStorage('favorites');
    const initializeData = list || [];

    useEffect(() => {
        console.log("Favorites:", initializeData);
    }, [initializeData]);

    return (
        <div className="favoritesContainer">
            <h1>Your Favorites</h1>
            {initializeData.length > 0 ? (
                <div className="favoritesList">
                    {initializeData.map((item: any) => (
                        <div key={item.id} className="favoriteItem">
                            <h2>{item.title}</h2>
                            <img src={item.image} alt={`${item.title} Image`} />
                        </div>
                    ))}
                </div>
            ) : (
                <p>No favorites yet.</p>
            )}
        </div>
    );
};

export default Favorites;
