import { useEffect, useState } from "react";

const useUpdateLocalStorage = (key: string, item: string) => {
    
    const getData = localStorage.getItem(key);
    const prevData = JSON.parse(getData!);
    const [list, setList] = useState<string[]>(prevData);

    useEffect(() => {
        if (list !== item) {
            const updatedList = [...list, item];
            setList(updatedList);
            localStorage.setItem(key, JSON.stringify(updatedList));
        }
    }, [list, key, item]);

    return {list};
}
 
export default useUpdateLocalStorage;
