import { useEffect, useState } from "react";
import { ItemInterface } from "../pages/Home";
import useLocalStorage from "./useLocalStorage";

const useUpdateLocalStorage = (key: string, item: ItemInterface | null) => {
    // Safely retrieve and parse data from localStorage
    const prevData = useLocalStorage(key); // Fallback to an empty array if no data
    const [newData, setNewData] = useState<ItemInterface>();
    useEffect(() => {
        const {id, title, image} = prevData;
        setNewData({id, title, image})
    }, [prevData])


    const [list, setList] = useState<ItemInterface[]>([newData]);

    useEffect(() => {
        if (item) {
            setList((prevList) => [...prevList, newData]);
            localStorage.setItem(key, JSON.stringify(list));
        }
    }, [item, key]); // Depend on item and key only

    return { list };
};

export default useUpdateLocalStorage;
