import { useEffect, useState } from "react";
import useFetch from "../hooks/useFetch";
import { useLocation } from "react-router-dom";
import { Console } from "console";


const Meal = () => {
    const APIKEY = 'ae98638f897c4eb79d6f212f141affb8';
    const location = useLocation();
    const queryParams = new URLSearchParams(location.search);
    const id = queryParams.get('id');
    const {data, loading, error} = useFetch(`https://api.spoonacular.com/recipes/${id}/information?apiKey=${APIKEY}&includeNutrition=false`);
    const [mealRecipe, setMealRecipe] = useState<any>();

    useEffect(() => {
        if (data) {
        setMealRecipe(data);
        }
    }, [data])


    return ( 
        <div className="foodRecipeContainer">
            {mealRecipe?.map(meal => 
                <div className="titleContainer">
                    <span className="title">{meal.title}</span>
                </div>
            )}
        </div>
     );
}
 
export default Meal;
